import React, { useEffect, useState } from "react";
import "./TipoEventosPage.css";
import Title from "../../Components/Title/Title";
import MainContent from "../../Components/MainContent/MainContent";
import ImageIllustrator from "../../Components/ImageIllustrator/ImageIllustrator";
import eventTypeImage from "../../assets/images/tipo-evento.svg";
import Container from "../../Components/Container/Container";
import { Button, Input } from "../../Components/FormComponents/FormComponents";
import api from "../../Services/Service";
import TableTp from "./TableTp/TableTp";
import Notification from "../../Components/Notification/Notification";
import Spinner from "../../Components/Spinner/Spinner";

const TipoEventosPage = () => {
  const [notifyUser, setNotifyUser] = useState({});
  const [showSpinner, setShowSpinner] = useState(true);
  const [frmEdit, setFrmEdit] = useState(false);
  const [titulo, setTitulo] = useState("");
  const [tipoEventos, setTipoEventos] = useState([]);
  const [idEvento, setIdEvento] = useState(null);

  useEffect(() => {
    async function getTipoEventos() {
      try {
        const promise = await api.get("/TiposEvento");
        setTipoEventos(promise.data);
      } catch (error) {
        console.log("Deu ruim na api");
        console.log(error);
      }
    }
    getTipoEventos();
  }, [tipoEventos]);

  async function handleSubmit(e) {
    e.preventDefault();

    //validar pelo menos 3 caracteres
    if (titulo.trim().length < 3) {
      alert("O titulo deve ter ao menos 3 caracteres");
      return;
    }
    //chamar api
    try {
      const retorno = await api.post("/TiposEvento", { titulo: titulo });
      console.log("Cadastrado com sucesso");
      setNotifyUser({
        titleNote: "Sucesso",
        textNote: `Cadastrado com sucesso!`,
        imgIcon: "success",
        imgAlt:
          "Imagem de ilustração de sucesso. Moça segurando um balão com símbolo de confirmação ok.",
        showMessage: true,
      });
      console.log(retorno.data);
      setTitulo(""); //limpa a variavel
    } catch (error) {
      console.log("deu ruim na api");
      console.log(error);
    }
  }

  async function handleUpdate(e) {
    e.preventDefault();
    try {
      // Salvar os daods
      const retorno = await api.put("/TiposEvento/" + idEvento, {
        titulo: titulo,
      });

      editActionAbort();

      // Atualizar o state(api get)
      const retornoGet = await api.get("/tiposEvento");
      setTipoEventos(retornoGet.data); // Atualiza o state da tabela

      setNotifyUser({
        titleNote: "Sucesso",
        textNote: `Atualizado com sucesso!`,
        imgIcon: "success",
        imgAlt:
          "Imagem de ilustração de sucesso. Moça segurando um balão com símbolo de confirmação ok.",
        showMessage: true,
      });
      
    } catch (error) {
      setNotifyUser({
        titleNote: "Erro",
        textNote: `erro na api`,
        imgIcon: "danger",
        imgAlt: "Imagem de ilustração de danger.",
        showMessage: true,
      });
    }
  }

  async function showUpdateForm(idElemento) {
    setFrmEdit(true);
    try {

      const retorno = await api.get("/TiposEvento/" + idElemento);

      setTitulo(retorno.data.titulo);
      setIdEvento(retorno.data.idTipoEvento);

    } catch (error) {

      setNotifyUser({
        titleNote: "Erro",
        textNote: `erro0 na api`,
        imgIcon: "danger",
        imgAlt: "Imagem de ilustração de danger.",
        showMessage: true,
      });
    }
  }

  function editActionAbort() {
    setFrmEdit(false);
    setTitulo("");
    setIdEvento(null);
  }

  async function handleDelete(id) {
    try {
      await api.delete(`/TiposEvento/${id}`);
      console.log("deletado com sucesso");

      const retornoGet = await api.get("/TiposEvento");
      setTipoEventos(retornoGet.data);

      setNotifyUser({
        titleNote: "Sucesso",
        textNote: `deletado com sucesso!`,
        imgIcon: "success",
        imgAlt:
          "Imagem de ilustração de sucesso. Moça segurando um balão com símbolo de confirmação ok.",
        showMessage: true,
      });
    } catch (error) {
      console.log("deu ruim na api");
      console.log(error);
    }
  }

  return (
    <MainContent>
      <Notification {...notifyUser} setNotifyUser={setNotifyUser} />
      {/* CADASTRO DE TIPO DE EVENTOS */}
      <section className="cadastro-evento-section">
        <Container>
          <div className="cadastro-evento__box">
            <Title titleText={"Página Tipos de Eventos"} />

            <ImageIllustrator alterText={""} imageRender={eventTypeImage} />

            <form
              className="ftipo-evento"
              onSubmit={frmEdit ? handleUpdate : handleSubmit}
            >
              {!frmEdit ? (
                <>
                  <Input
                    type={"text"}
                    id={"titulo"}
                    name={"titulo"}
                    placeholder={"titulo"}
                    required={"required"}
                    value={titulo}
                    manipulationFunction={(e) => {
                      setTitulo(e.target.value);
                    }}
                  />

                  {titulo}

                  <Button
                    type={"submite"}
                    id={"cadastrar"}
                    name={"cadastrar"}
                    textButton={"cadastrar"}
                  />
                </>
              ) : (
                <>
                  {/* EDITAR */}
                  <Input
                    id="titulo"
                    placeholder="Titulo"
                    name="titulo"
                    type="text"
                    required="required"
                    value={titulo}
                    manipulationFunction={(e) => {
                      setTitulo(e.target.value);
                    }}
                  />

                  <div className="buttons-editbox">
                    <Button
                      textButton="atualizar"
                      id="atualizar"
                      name="atualizar"
                      type="submit"
                      additionalClass="button-component--middle"
                    />
                    <Button
                      textButton="Cancelar"
                      id="cancelar"
                      name="cancelar"
                      type="button"
                      manipulationFunction={editActionAbort}
                      additionalClass="button-component--middle"
                    />
                  </div>
                </>
              )}
            </form>
          </div>
        </Container>
      </section>

      {/* LISTAGEM DE TIPO DE EVENTOS */}
      <section className="lista-eventos-section">
        <Container>
          <Title titleText={"Lista de tipo de eventos"} color="white" />

          <TableTp
            dados={tipoEventos}
            fnUpdate={(idElemento) => showUpdateForm(idElemento)}
            fnDelete={handleDelete}
          />
        </Container>
      </section>
    </MainContent>
  );
};

export default TipoEventosPage;
